const initialState = {
    items: [],
    totalPrice: 0,
    totalCount: 0
}

export default (state = initialState, { type, payload }) => {
    switch (type) {

    case "ADD_ITEM":
        return { 
            ...state, 
            items: [...state.items, payload.item],
            totalPrice: state.totalPrice+payload.sell
        }

    case "REMOVE_ITEM":

        console.log("Это происходит");
        return {
            ...state,
            items: state.items.filter(item => item.id === payload)
        }

    default:
        return state
    }
}
