export const fetchItems = () => (dispatch) => {
    axios.get("http://localhost:1111/items").then(({ data }) => {
        dispatch(setItems(data));
    });
}

export const setItems = (payload) => ({
    type: "SET_ITEMS",
    payload: payload
});
