import React from 'react';
import { CardItem } from '../main';
import {useDispatch, useSelector} from 'react-redux';
import { setActiveItem } from '../../redux/actions';

export default function ListCardItem({cards}) {

    const dispatch = useDispatch();
    const { activeItem } = useSelector(({activeItem}) => {
        return {
            activeItem: activeItem.item
        }
    });
    const cats = ["Привелегии", "Кейсы", "Вещи", "Валюта"]

    return (
        <div>
            <div class="category">
                {cats && cats.map((name, index) => (
                    <div 
                    className={activeItem === index ? 'category__item-use' : 'category__item'}
                    onClick={() => dispatch(setActiveItem(index, name))}
                    >
                        <span>{name}</span>
                    </div>
                ))}
            </div>

            <div class="cards">
                {cards.map(card => {
                    if (cards.type == activeItem.name) {
                        return (<CardItem citem={card} />);
                    };
                    return '';
                })}
            </div>
        </div>
    )
};
