import React from 'react';
import { CardItem } from '../main';
import {useDispatch, useSelector} from 'react-redux';
import { setActiveItem } from '../../redux/actions';
import { showModalWindow, hideModalWindow } from '../../redux/actions/modal';

export default function ListCardItem({citems}) {

    const dispatch = useDispatch();
    const { activeItem } = useSelector(({activeItem, modal}) => {
        return {
            activeItem: activeItem.item
        };
    });
    const cats = ["Привелегии", "Кейсы", "Вещи", "Валюта"]

    React.useEffect(() => {
        dispatch()
    }, []);

    return (
        <div>
            <div class="category">
                {cats && cats.map((name, index) => (
                    <div 
                    className={activeItem === index ? 'category__item-use' : 'category__item'}
                    onClick={() => dispatch(setActiveItem(index))}
                    >
                        <span>{name}</span>
                    </div>
                ))}
            </div>

            <div class="cards">
                {citems.map(citem => <CardItem citem={citem} /> )}
            </div>
        </div>
    )
};
