import React from 'react';
import { CardItem } from '../main';

export default function ListCardItem({citems}) {

    const [activeItem, setActiveItem] = React.useState();
    const cats = ["Привелегии", "Кейсы", "Вещи", "Валюта"]

    return (
        <div>
            <div class="category">
                {cats && cats.map((name, index) => (
                    <div className={activeItem === index ? 'category__item-use' : 'category__item'}><span>{name}</span></div>
                ))}
            </div>

            <div class="cards">
                {citems.map(citem => <CardItem citem={citem} /> )}
            </div>
        </div>
    )
};
