import React from 'react';
import Plmin from './Plmin';
import {useDispatch} from 'react-redux';
import { hideModalWindow } from '../../redux/actions/modal';

export default function Modal({ item, input, plmin }) {

    const addToCart = () => {
        console.log("Кнопка нажата");
    };

    const [value, setValue] = React.useState('');
    const sortRef = React.useRef();

    const dispatch = useDispatch();

    React.useEffect(() => {
        document.body.addEventListener('click', handleOutsideClick);
    }, []);

    const handleOutsideClick = (event) => {
        const path = event.path || (event.composedPath && event.composedPath());
        if (!path.includes(sortRef.current)) {
          dispatch(hideModalWindow);
         
        }
      };

    return (
        <div class="modal" ref={sortRef}>
            <div class="modal__body">
                <div class="modal__background">
                    <img src="" alt="" />
                </div>

                <div class="modal__info">
                    <div class="info">

                        <div class="info__title">{item.name} <span>{item.sell} ₽</span></div>
                        <div class="info__subtitle">Добавление в корзину</div>
                        <div class="info__type"><span>Тип:</span> {item.type}</div>
                        <div class="info__desc">
                            <span>Описание:</span>
                            <p>{item.desc}</p>
                        </div>

                        {input ? <input type="text" class="info__count" value={value} onChange={(e) => setValue(e.target.value)} placeholder="Введите кол-во" /> : <span></span>}
                        {plmin ? <div class="info__mp"><Plmin /></div> : <span></span>}

                        <div class="info__button" onClick={addToCart}>
                            <span>Добавить</span> <img src="http://localhost:9999/assets/cartbutton.svg" alt="" />
                        </div>
                        
                    </div>
                </div>
            </div>
        </div>
    )
}
