import React from 'react';
import {NavLink} from 'react-router-dom';

let MainHeader = () => {
    return (
        <NavLink to="/cart">
            <div>
                <span>0 руб</span>
                <img src="http://localhost:9999/assets/palka.svg" alt="" />
                <img src="http://localhost:9999/assets/cart.svg" alt="" />
            </div>
        </NavLink>
    )
};

let CartHeader = () => {
    return (
        <span><NavLink to="/">В магазин</NavLink></span>
    )
};

export default function Header({ type }) {
    return (
        <header>
            <div class="nav">
                <div class="nav__body">
                    <div class="nav__item-logo">
                        <img src="http://localhost:9999/assets/logo.svg" alt="" class="nav__img" />
                        <span>MineStand</span>
                    </div>
                    <div class="nav__item-btn">
                        <div class="nav__btn">
                            { type ? <CartHeader /> : <MainHeader />}
                        </div>
                    </div>
                </div>
            </div>
        </header>
    )
};
