import React from 'react';
import { Modal } from '../utils';

export default function CardItem({citem}) {

    const [modal, getModal] = React.useState(false);

    return (
        <>  
            <div class="card">
                <div class="card__see">
                    <img src={citem.ava} alt="" />
                </div>
                <div class="card__name">{citem.name}</div>
                <div class="card__desc">{citem.desc}</div>

                <div class="card__final">
                    <div class="card__final__sell">{citem.sell} ₽</div>
                    <div class="card__final__button" onClick={() => getModal(true)}><span>Выбрать</span></div>
                </div>
            </div>
        </>
    )
};
