import './App.css';
import React from 'react';
import { Cart, Main } from './pages';
import { Route } from 'react-router-dom';
import { BrowserRouter as Router } from 'react-router-dom';
import {useDispatch} from 'react-redux'
import { setLoading, setItems } from './redux/actions/cards';
import axios from 'axios';

function App() {

  const dispatch = useDispatch();

  React.useEffect(() => {
    console.log("Действие при старте приложения запущено.")


    axios.get("http://localhost:1111/items").then(({ data }) => {
        console.log("Данные получены и выгружены в хранилище.")
    });
  }, []);

  return (
    <Router>

      <Route path="/" exact component={Main} />
      <Route path="/cart" component={Cart} />

    </Router>
  );
}

export default App;
