import React from 'react';
import { Header, Footer, Modal } from '../components/utils';
import { Welcome, ListBuyers, ListCardItems, Help  } from '../components/main';
import { showModalWindow, hideModalWindow } from '../redux/actions/modal';
import {useDispatch, useSelector} from 'react-redux';
import axios from 'axios';
import { setItems } from '../redux/actions';

export default function Main() {

    const { showModal, settings, cards } = useSelector(({modal, cards}) => {
        return {
            showModal: modal.showModal,
            settings: modal.settings,
            cards: cards.items
        }
    });
    const dispatch = useDispatch();

    axios.get("http://localhost:1111/items").then(({ data }) => {
        dispatch(setItems(data));
    });

    console.log(cards);

    const citems = [
        {ava: "", type: "Привелегия", name: "Archer", desc: "Это самая лучшая привелегия", sell: "200"},
        {ava: "", type: "Привелегия", name: "Platinum", desc: "Это самая лучшая привелегия", sell: "200"},
        {ava: "", type: "Привелегия", name: "Archer", desc: "Это самая лучшая привелегия", sell: "200"},
        {ava: "", type: "Привелегия", name: "Ultra", desc: "Это самая лучшая привелегия", sell: "200"},
        {ava: "", type: "Привелегия", name: "Archer", desc: "Это самая лучшая привелегия", sell: "200"},
        {ava: "", type: "Привелегия", name: "Premium", desc: "Это самая лучшая привелегия", sell: "200"},
        {ava: "", type: "Привелегия", name: "Archer", desc: "Это самая лучшая привелегия", sell: "200"}
    ]

    const buyers = [
        {ava: "http://localhost:9999/assets/buys.svg", nickname: "TheStrikeM", type: "Привелегия", item: "Archer", sell: "200"},
        {ava: "http://localhost:9999/assets/buys.svg", nickname: "TheStrikeM", type: "Привелегия", item: "Archer", sell: "200"},
        {ava: "http://localhost:9999/assets/buys.svg", nickname: "TheStrikeM", type: "Привелегия", item: "Archer", sell: "200"},
        {ava: "http://localhost:9999/assets/buys.svg", nickname: "TheStrikeM", type: "Привелегия", item: "Archer", sell: "200"},
    ]

    return (
        <div>
            <Header type={false} />

            {showModal ? <Modal item={settings} input={false} plmin={false} /> : ''}

            <div className="wrapper">
                <Welcome />
                <ListCardItems cards={cards} />
                <Help />
                <ListBuyers buyers={buyers} />
                <Footer />
            </div>
        </div>
    )
};
